SELECT HD.SHIP_ORDER_KEY AS shipOrderKey, HD.WAREHOUSE_KEY AS warehouseKey, HD.SHIP_TYPE AS shipType, HD.DOC_STATUS AS docStatus, HD.DOC_DATE AS docDate, HD.MEMBER_GRADE AS memberGrade, HD.OWNER_KEY AS ownerKey, HD.REQ_SHIP_DATE AS reqShipDate, HD.SUPPLIER_CODE AS supplierCode, HD.CUSTOMER_NAME AS customerName, HD.ORDER_TYPE AS orderType, HD.TRANSPORT_TYPE AS transportType, HD.ORDER_NO AS orderNo, HD.COURIER_BOX_PASSWORD AS courierBoxPassword, HD.RECIPIENT_NAME AS recipientName, HD.RECIPIENT_TEL_NO AS recipientTelNo, HD.ROAD_ADDR AS roadAddr, HD.ROAD_ADDR_DETAIL AS roadAddrDetail, HD.CUSTOMER_ID AS customerId, HD.CUSTOMER_TEL_NO AS customerTelNo, HD.ZIP_CODE AS zipCode, HD.CUSTOMER_PHONE_NO AS customerPhoneNo, HD.NEW_ZIP_CODE AS newZipCode, HD.JIBUN_BASIC_ADDR AS jibunBasicAddr, HD.JIBUN_DETAIL_ADDR AS jibunDetailAddr, HD.REMARK AS remark, NVL( HD.BASE_ADDR_TYPE, ' ') AS baseAddrType , HD.ORDER_ADDR_GUGUN AS orderAddrGugun, HD.ORDER_ADDR_DONG AS orderAddrDong, HD.COURIER_CODE AS courierCode, HD.NOTE AS note, HD.CS_NOTICE AS csNotice, HD.CUSTOMER_MESSAGE AS customerMessage, HD.REGION_GROUP_CODE AS regionGroupCode, HD.REGION_CODE AS regionCode, HD.DELIVERY_ROUND AS deliveryRound, HD.ALLOC_SEQ AS allocSeq, CASE WHEN HD.ORIGIN_INVOICE_NO IS NULL THEN HD.INVOICE_NO || '-0001' ELSE HD.ORIGIN_INVOICE_NO || '-0001' END AS originInvoiceNo , NVL( HD.ALLOC_TYPE, '') AS allocType, (CASE WHEN HD.MEMBER_GRADE = '0003' THEN 'Y' ELSE 'N' END) AS specialMgntCustYn , HD.MANUAL_PROC_YN AS manualProcYn, HD.BOX_SPLIT_CHECK_YN AS boxSplitCheckYn, HD.INVOICE_NO AS invoiceNo , DT.SHIP_ORDER_ITEM_SEQ AS shipOrderItemSeq, DT.ITEM_STATUS AS itemStatus, DT.QTY_ORIGIN_ORDER AS qtyOriginOrder, DT.QTY_SHIP_ORDER AS qtyShipOrder, DT.QTY_PICKING AS qtyPicking, DT.QTY_ALLOCATED AS qtyAllocated, DT.QTY_PACKING AS qtyPacking, DT.QTY_SHIP_COMPLETED AS qtyShipCompleted, DT.QTY_SHIP_CANCELLED AS qtyShipCancelled, DT.QTY_BY_UOM AS qtyByUom, DT.MEASURE_KEY AS measureKey, DT.UOM_KEY AS uomKey, DT.UOM_QTY AS uomQty, DT.DEFAULT_UOM_KEY AS defaultUomKey, DT.DEFAULT_UOM_QTY AS defaultUomQty, DT.SKU_NAME AS skuName, DT.SKU_SUB_NAME AS skuSubName, DT.SKU_ALTER_CODE AS skuAlterCode, DT.SKU_GROUP_01 AS skuGroup01, DT.SKU_GROUP_02 AS skuGroup02, DT.SKU_GROUP_03 AS skuGroup03, DT.SKU_GROUP_04 AS skuGroup04, DT.SELLING_TYPE AS sellingType, DT.GROSS_WEIGHT AS grossWeight, DT.NET_WEIGHT AS netWeight, DT.WEIGHT_UNIT AS weightUnit, DT.CBM AS cbm, DT.SKU_OPTION_NAME AS skuOptionName, DT.SKU_SHOPMALL_NAME AS skuShopmallName, DT.REMARK AS dtRemark , DT.GROUP_NO AS groupNo, DT.WORK_BATCH_NO AS workBatchNo, DT.QPS_NUM AS qpsNum, DT.WMS_BATCH_YMD AS wmsBatchYmd , DT.SKU_CODE AS skuCode, (CASE WHEN SM.LENGTH = 0 THEN DT.LENGTH ELSE SM.LENGTH END) AS skuDepth, (CASE WHEN SM.WIDTH = 0 THEN DT.WIDTH ELSE SM.WIDTH END) AS skuWidth, (CASE WHEN SM.HEIGHT = 0 THEN DT.HEIGHT ELSE SM.HEIGHT END) AS skuHeight, SUM(DT.QTY_SHIP_ORDER) AS ordQty FROM o_wcs.TB_WIF_SHIPMENT_HDR HD, o_wcs.TB_WIF_SHIPMENT_DTL DT, o_wcs.TB_COM_SKU_MST SM WHERE HD.SHIP_ORDER_KEY = DT.SHIP_ORDER_KEY AND SM.SKU_CODE = DT.SKU_CODE AND SM.OWNER = HD.OWNER_KEY AND HD.RECEIVE_INTF_YN = 'N'
GROUP BY HD.SHIP_ORDER_KEY , HD.WAREHOUSE_KEY , HD.SHIP_TYPE , HD.DOC_STATUS , HD.DOC_DATE , HD.MEMBER_GRADE , HD.OWNER_KEY , HD.REQ_SHIP_DATE , HD.SUPPLIER_CODE , HD.CUSTOMER_NAME , HD.ORDER_TYPE , HD.TRANSPORT_TYPE , HD.ORDER_NO , HD.COURIER_BOX_PASSWORD , HD.RECIPIENT_NAME , HD.RECIPIENT_TEL_NO , HD.ROAD_ADDR , HD.ROAD_ADDR_DETAIL , HD.CUSTOMER_ID , HD.CUSTOMER_TEL_NO , HD.ZIP_CODE , HD.CUSTOMER_PHONE_NO , HD.NEW_ZIP_CODE , HD.JIBUN_BASIC_ADDR , HD.JIBUN_DETAIL_ADDR , HD.REMARK , HD.BASE_ADDR_TYPE , HD.ORDER_ADDR_GUGUN , HD.ORDER_ADDR_DONG , HD.COURIER_CODE , HD.NOTE , HD.CS_NOTICE , HD.CUSTOMER_MESSAGE , HD.REGION_GROUP_CODE , HD.REGION_CODE , HD.DELIVERY_ROUND , HD.ALLOC_SEQ , HD.ORIGIN_INVOICE_NO , HD.ALLOC_TYPE , HD.MEMBER_GRADE , HD.MANUAL_PROC_YN , HD.BOX_SPLIT_CHECK_YN , HD.INVOICE_NO , DT.SHIP_ORDER_ITEM_SEQ , DT.ITEM_STATUS , DT.QTY_ORIGIN_ORDER , DT.QTY_SHIP_ORDER , DT.QTY_PICKING , DT.QTY_ALLOCATED , DT.QTY_PACKING , DT.QTY_SHIP_COMPLETED , DT.QTY_SHIP_CANCELLED , DT.QTY_BY_UOM , DT.MEASURE_KEY , DT.UOM_KEY , DT.UOM_QTY , DT.DEFAULT_UOM_KEY , DT.DEFAULT_UOM_QTY , DT.SKU_NAME , DT.SKU_SUB_NAME , DT.SKU_ALTER_CODE , DT.SKU_GROUP_01 , DT.SKU_GROUP_02 , DT.SKU_GROUP_03 , DT.SKU_GROUP_04 , DT.SELLING_TYPE , DT.GROSS_WEIGHT , DT.NET_WEIGHT , DT.WEIGHT_UNIT , DT.CBM , DT.SKU_OPTION_NAME , DT.SKU_SHOPMALL_NAME , DT.REMARK , DT.GROUP_NO , DT.WORK_BATCH_NO , DT.QPS_NUM , DT.WMS_BATCH_YMD , DT.SKU_CODE, (CASE WHEN SM.LENGTH = 0 THEN DT.LENGTH ELSE SM.LENGTH END) , (CASE WHEN SM.WIDTH = 0 THEN DT.WIDTH ELSE SM.WIDTH END) , (CASE WHEN SM.HEIGHT = 0 THEN DT.HEIGHT ELSE SM.HEIGHT END) ORDER BY HD.SHIP_ORDER_KEY ASC, DT.SHIP_ORDER_ITEM_SEQ ASC

