/* [BoxRecomMapper.xml] updateWifShipmentHdrList */ UPDATE o_wcs.TB_WIF_SHIPMENT_HDR HD SET RECEIVE_INTF_YN = #{receiveIntfYn} , RECEIVE_INTF_DT = SYSDATE , RECEIVE_INTF_CODE = #{receiveIntfCode} , RECEIVE_INTF_MEMO = #{receiveIntfMemo} , MODIFIED_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') , MODIFIED_TIME = TO_CHAR(SYSDATE, 'HH24MISS') , MODIFIED_USER = 'REC_BATCH' WHERE EXISTS ( SELECT 1 FROM (
SELECT #{item.shipOrderKey} AS SHIP_ORD_KEY FROM DUAL
) A WHERE A.SHIP_ORD_KEY = HD.SHIP_ORDER_KEY )
