mapper.update:
  /* [InvoicePrintCompletMapper.xml] updateInvoicePrintComplet */
  	UPDATE o_wcs.TB_DOC_INVOICE_HDR
		SET
			INVOICE_PRINT_INTF_YN =  #{invoicePrintIntfYn},
			INVOICE_PRINT_INTF_DT =  SYSDATE,
			INVOICE_PRINT_INTF_CODE =  #{invoicePrintIntfCode},
			INVOICE_PRINT_INTF_MEMO = #{invoicePrintIntfMemo} ,
			MODIFIED_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') ,
			MODIFIED_TIME = TO_CHAR(SYSDATE, 'HH24MISS') ,
			MODIFIED_USER = #{modifiedUser}
	WHERE INVOICE_NO = #{invoiceNo}
	  AND WAREHOUSE_KEY = #{warehouseKey}
	  AND SHIP_ORDER_KEY = #{shipOrderKey}

/* [InvoicePrintCompletMapper.xml] updateInvoicePrintCompletList */ MERGE INTO o_wcs.TB_DOC_INVOICE_HDR DIH USING (
SELECT #{item.invoiceNo} AS INVOICE_NO , #{item.warehouseKey} AS WAREHOUSE_KEY , #{item.shipOrderKey} AS SHIP_ORDER_KEY , #{item.invoicePrintIntfYn} AS INVOICE_PRINT_INTF_YN , #{item.invoicePrintIntfCode} AS INVOICE_PRINT_INTF_CODE , #{item.invoicePrintIntfMemo} AS INVOICE_PRINT_INTF_MEMO , #{item.modifiedUser} AS MODIFIED_USER FROM DUAL
) TB ON ( DIH.INVOICE_NO = TB.INVOICE_NO AND DIH.WAREHOUSE_KEY = TB.WAREHOUSE_KEY AND DIH.SHIP_ORDER_KEY = TB.SHIP_ORDER_KEY ) WHEN MATCHED THEN UPDATE
DIH.INVOICE_PRINT_INTF_YN = TB.INVOICE_PRINT_INTF_YN, DIH.INVOICE_PRINT_INTF_DT = SYSDATE, DIH.INVOICE_PRINT_INTF_CODE = TB.INVOICE_PRINT_INTF_CODE, DIH.INVOICE_PRINT_INTF_MEMO = TB.INVOICE_PRINT_INTF_MEMO , DIH.MODIFIED_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') , DIH.MODIFIED_TIME = TO_CHAR(SYSDATE, 'HH24MISS') , DIH.MODIFIED_USER = TB.MODIFIED_USER
