mapper.update:
  /* [InvoiceSortCompletMapper.xml] updateInvoiceSortComplet */
  	UPDATE o_wcs.TB_DOC_INVOICE_HDR
		SET
			INVOICE_SORT_INTF_YN =  #{invoiceSortIntfYn},
			INVOICE_SORT_INTF_DT =  SYSDATE,
			INVOICE_SORT_INTF_CODE =  #{invoiceSortIntfCode},
			INVOICE_SORT_INTF_MEMO = #{invoiceSortIntfMemo} ,
			MODIFIED_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') ,
			MODIFIED_TIME = TO_CHAR(SYSDATE, 'HH24MISS') ,
			MODIFIED_USER = #{modifiedUser}
	WHERE INVOICE_NO = #{invoiceNo}
	  AND WAREHOUSE_KEY = #{warehouseKey}
	  AND SHIP_ORDER_KEY = #{shipOrderKey}

/* [InvoiceSortCompletMapper.xml] updateInvoiceSortCompletList */ MERGE INTO o_wcs.TB_DOC_INVOICE_HDR HDR USING (
SELECT #{item.invoiceNo} AS INVOICE_NO, #{item.warehouseKey} AS WAREHOUSE_KEY, #{item.shipOrderKey} AS SHIP_ORDER_KEY , #{item.invoiceSortIntfYn} AS INVOICE_SORT_INTF_YN , #{item.invoiceSortIntfCode} AS INVOICE_SORT_INTF_CODE , #{item.invoiceSortIntfMemo} AS INVOICE_SORT_INTF_MEMO , #{item.modifiedUser} AS MODIFIED_USER FROM DUAL
) TB ON ( HDR.INVOICE_NO = TB.INVOICE_NO AND HDR.WAREHOUSE_KEY = TB.WAREHOUSE_KEY AND HDR.SHIP_ORDER_KEY = TB.SHIP_ORDER_KEY ) WHEN MATCHED THEN UPDATE
HDR.INVOICE_SORT_INTF_YN = TB.INVOICE_SORT_INTF_YN , HDR.INVOICE_SORT_INTF_DT = SYSDATE , HDR.INVOICE_SORT_INTF_CODE = TB.INVOICE_SORT_INTF_CODE , HDR.INVOICE_SORT_INTF_MEMO = TB.INVOICE_SORT_INTF_MEMO , HDR.MODIFIED_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') , HDR.MODIFIED_TIME = TO_CHAR(SYSDATE, 'HH24MISS') , HDR.MODIFIED_USER = TB.MODIFIED_USER
