mapper.update:
/* [PackQpsCompletMapper.xml] updatePackQpsCompletList */ MERGE INTO o_wcs.TB_DOC_INVOICE_HDR HDR USING (
SELECT #{item.invoiceUidKey} AS INVOICE_UID_KEY, #{item.packCmptIntfYn} AS PACK_CMPT_INTF_YN , #{item.packCmptIntfCode} AS PACK_CMPT_INTF_CODE , #{item.packCmptIntfMemo} AS PACK_CMPT_INTF_MEMO , #{item.modifiedUser} AS MODIFIED_USER FROM DUAL
) TB ON ( HDR.INVOICE_UID_KEY = TB.INVOICE_UID_KEY ) WHEN MATCHED THEN UPDATE
HDR.PACK_CMPT_INTF_YN = TB.PACK_CMPT_INTF_YN, HDR.PICK_CMPT_INTF_DT = SYSDATE, HDR.PACK_CMPT_INTF_CODE = TB.PACK_CMPT_INTF_CODE, HDR.PACK_CMPT_INTF_MEMO = TB.PACK_CMPT_INTF_MEMO , HDR.MODIFIED_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') , HDR.MODIFIED_TIME = TO_CHAR(SYSDATE, 'HH24MISS') , HDR.MODIFIED_USER = TB.MODIFIED_USER
