mapper.update:
  /* [ToteCellExceptTxnMapper.xml] updateToteCellExceptTxn */
  	UPDATE o_wcs.TB_QPS_CELL_EXCEPT_TXN
		SET
			EXCEPT_IF_YN =  #{exceptIfYn},
			EXCEPT_IF_DT =  SYSDATE,
			EXCEPT_IF_RET_CODE =  #{exceptIfRetCode},
			EXCEPT_IF_RET_MESSAGE = #{exceptIfRetMessage} ,
			MODIFIED_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') ,
			MODIFIED_TIME = TO_CHAR(SYSDATE, 'HH24MISS') ,
			MODIFIED_USER = #{modifiedUser}
	WHERE TOTE_ID = #{toteId}
	  AND TOTE_ID_SEQ = #{toteIdSeq}
	  AND CELL_EXCEPT_SEQ = #{cellExceptSeq}

/* [ToteCellExceptTxnMapper.xml] updateToteCellExceptTxnList */ MERGE INTO o_wcs.TB_QPS_CELL_EXCEPT_TXN HDR USING (
SELECT #{item.toteId} AS TOTE_ID, #{item.toteIdSeq} AS TOTE_ID_SEQ, #{item.cellExceptSeq} AS CELL_EXCEPT_SEQ, #{item.exceptIfYn} AS EXCEPT_IF_YN , #{item.exceptIfRetCode} AS EXCEPT_IF_RET_CODE , #{item.exceptIfRetMessage} AS EXCEPT_IF_RET_MESSAGE , #{item.modifiedUser} AS MODIFIED_USER FROM DUAL
) TB ON ( HDR.TOTE_ID = TB.TOTE_ID AND HDR.TOTE_ID_SEQ = TB.TOTE_ID_SEQ AND HDR.CELL_EXCEPT_SEQ = TB.CELL_EXCEPT_SEQ ) WHEN MATCHED THEN UPDATE
HDR.EXCEPT_IF_YN = TB.EXCEPT_IF_YN, HDR.EXCEPT_IF_DT = SYSDATE, HDR.EXCEPT_IF_RET_CODE = TB.EXCEPT_IF_RET_CODE, HDR.EXCEPT_IF_RET_MESSAGE = TB.EXCEPT_IF_RET_MESSAGE, HDR.MODIFIED_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') , HDR.MODIFIED_TIME = TO_CHAR(SYSDATE, 'HH24MISS') , HDR.MODIFIED_USER = TB.MODIFIED_USER
