mapper.update:
  /* [ToteScanMapper.xml] updateToteScan */
  	UPDATE o_wcs.TB_WIF_PICK_TOTE_HDR
		SET
			TOTE_SCAN_IF_YN =  #{toteScanIfYn},
			TOTE_SCAN_IF_DT =  SYSDATE,
			TOTE_SCAN_IF_RET_CODE =  #{toteScanIfRetCode},
			TOTE_SCAN_IF_RET_MESSAGE = #{toteScanIfRetMessage} 
	WHERE WIF_PICK_TOTE_HDR_SEQ = #{toteUniqueNo}

/* [ToteScanMapper.xml] updateToteScanList */ MERGE INTO o_wcs.TB_WIF_PICK_TOTE_HDR HDR USING (
SELECT #{item.toteUniqueNo} AS TOTE_UNIQUE_NO, #{item.toteScanIfYn} AS TOTE_SCAN_IF_YN , #{item.toteScanIfRetCode} AS TOTE_SCAN_IF_RET_CODE , #{item.toteScanIfRetMessage} AS TOTE_SCAN_IF_RET_MESSAGE FROM DUAL
) TB ON ( HDR.WIF_PICK_TOTE_HDR_SEQ = TB.TOTE_UNIQUE_NO ) WHEN MATCHED THEN UPDATE
HDR.TOTE_SCAN_IF_YN = TB.TOTE_SCAN_IF_YN, HDR.TOTE_SCAN_IF_DT = SYSDATE, HDR.TOTE_SCAN_IF_RET_CODE = TB.TOTE_SCAN_IF_RET_CODE, HDR.TOTE_SCAN_IF_RET_MESSAGE = TB.TOTE_SCAN_IF_RET_MESSAGE
