/* [WcsToDasMapper.xml] updateWorkBatchOrderList */ MERGE INTO O_WCS.TB_ORD_SHIPMENT_DTL DTL USING (
SELECT #{item.shipUidKey} AS SHIP_UID_KEY , #{item.shipUidItemSeq} AS SHIP_UID_ITEM_SEQ , #{item.dasIntfYn} AS DAS_INTF_YN , #{item.modifiedUser} AS MODIFIED_USER FROM DUAL
) TB ON( DTL.SHIP_UID_KEY = TB.SHIP_UID_KEY AND DTL.SHIP_UID_ITEM_SEQ = TB.SHIP_UID_ITEM_SEQ ) WHEN MATCHED THEN UPDATE
DTL.DAS_INTF_YN = TB.DAS_INTF_YN, DTL.DAS_INTF_DT = SYSDATE, DTL.MODIFIED_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') , DTL.MODIFIED_TIME = TO_CHAR(SYSDATE, 'HH24MISS') , DTL.MODIFIED_USER = TB.MODIFIED_USER

/* [WcsToDasMapper.xml] createDocInfo */ { CALL DECLARE BEGIN
MERGE INTO O_WCS.TB_DOC_INVOICE_HDR IH USING (
SELECT INVOICE_NO , WAREHOUSE_KEY , SHIP_UID_KEY , SHIP_ORDER_KEY , DOC_DATE , 'DAS' AS INVOICE_PRINT_SYSTEM , ALLOC_TYPE , CELL_ID , STATION_ID , ORIGIN_INVOICE_NO , PACK_BOX_SPLIT_YN , PACK_BOX_TYPE_RECOM , SPECIAL_MGNT_CUST_YN , '20' AS INVOICE_STATUS , 'Y' AS INVOICE_NO_PRT_YN , 0 AS SPLIT_INVOICE_CNT , REGION_GROUP_CODE , REGION_CODE , DELIVERY_ROUND , ORDER_NO , REUSABLE_PACKAGE_YN , SHIPMENT_CNCL_YN AS INVOICE_CNCL_YN , SHIPMENT_CNCL_DATE || SHIPMENT_CNCL_TIME AS INVOICE_CNCL_YMD FROM O_WCS.TB_ORD_SHIPMENT_HDR WHERE DOC_DATE = TO_CHAR(SYSDATE-7/24, 'YYYYMMDD') AND ORDER_NO = #{item.orderNo} AND INVOICE_NO = #{item.invoiceNo}
) T2 ON( IH.INVOICE_NO = T2.INVOICE_NO AND IH.WAREHOUSE_KEY = T2.WAREHOUSE_KEY ) WHEN MATCHED THEN UPDATE
SHIP_UID_KEY = T2.SHIP_UID_KEY , SHIP_ORDER_KEY = T2.SHIP_ORDER_KEY , DOC_DATE = T2.DOC_DATE , INVOICE_PRINT_SYSTEM = T2.INVOICE_PRINT_SYSTEM , CELL_ID = T2.CELL_ID , STATION_ID = T2.STATION_ID , ORIGIN_INVOICE_NO = T2.ORIGIN_INVOICE_NO , PACK_BOX_SPLIT_YN = T2.PACK_BOX_SPLIT_YN , PACK_BOX_TYPE_RECOM = T2.PACK_BOX_TYPE_RECOM , SPECIAL_MGNT_CUST_YN = T2.SPECIAL_MGNT_CUST_YN , INVOICE_STATUS = T2.INVOICE_STATUS , INVOICE_NO_PRT_YN = T2.INVOICE_NO_PRT_YN , INVOICE_NO_PRT_YMD = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') , SPLIT_INVOICE_CNT = T2.SPLIT_INVOICE_CNT , REGION_GROUP_CODE = T2.REGION_GROUP_CODE , REGION_CODE = T2.REGION_CODE , DELIVERY_ROUND = T2.DELIVERY_ROUND , ORDER_NO = T2.ORDER_NO , REUSABLE_PACKAGE_YN = T2.REUSABLE_PACKAGE_YN , INVOICE_CNCL_YN = T2.INVOICE_CNCL_YN , INVOICE_CNCL_YMD = T2.INVOICE_CNCL_YMD , MODIFIED_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') , MODIFIED_TIME = TO_CHAR(SYSDATE, 'HH24MISS') , MODIFIED_USER = 'DAS_BATCH'
WHEN NOT MATCHED THEN INSERT ( INVOICE_UID_KEY , INVOICE_NO , WAREHOUSE_KEY , SHIP_UID_KEY , SHIP_ORDER_KEY , DOC_DATE , INVOICE_PRINT_SYSTEM , CELL_ID , STATION_ID , ORIGIN_INVOICE_NO , PACK_BOX_SPLIT_YN , PACK_BOX_TYPE_RECOM , SPECIAL_MGNT_CUST_YN , INVOICE_STATUS , INVOICE_NO_PRT_YN , INVOICE_NO_PRT_YMD , SPLIT_INVOICE_CNT , REGION_GROUP_CODE , REGION_CODE , DELIVERY_ROUND , ORDER_NO , REUSABLE_PACKAGE_YN , ALLOC_TYPE , INVOICE_CNCL_YN , INVOICE_CNCL_YMD , CREATED_DATE , CREATED_TIME , CREATED_USER , MODIFIED_DATE , MODIFIED_TIME , MODIFIED_USER )VALUES( 'WI' || LPAD(( O_WCS.SEQ_DOC_INVOICE_UID.NEXTVAL), 10, '0') , T2.INVOICE_NO , T2.WAREHOUSE_KEY , T2.SHIP_UID_KEY , T2.SHIP_ORDER_KEY , T2.DOC_DATE , T2.INVOICE_PRINT_SYSTEM , T2.CELL_ID , T2.STATION_ID , T2.ORIGIN_INVOICE_NO , T2.PACK_BOX_SPLIT_YN , T2.PACK_BOX_TYPE_RECOM , T2.SPECIAL_MGNT_CUST_YN , T2.INVOICE_STATUS , T2.INVOICE_NO_PRT_YN , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') , T2.SPLIT_INVOICE_CNT , T2.REGION_GROUP_CODE , T2.REGION_CODE , T2.DELIVERY_ROUND , T2.ORDER_NO , T2.REUSABLE_PACKAGE_YN , T2.ALLOC_TYPE , T2.INVOICE_CNCL_YN , T2.INVOICE_CNCL_YMD , TO_CHAR(SYSDATE, 'YYYYMMDD') , TO_CHAR(SYSDATE, 'HH24MISS') , 'DAS_BATCH' , TO_CHAR(SYSDATE, 'YYYYMMDD') , TO_CHAR(SYSDATE, 'HH24MISS') , 'DAS_BATCH' );
DELETE FROM O_WCS.TB_DOC_INVOICE_DTL WHERE INVOICE_NO = #{item.invoiceNo};
INSERT INTO O_WCS.TB_DOC_INVOICE_DTL ( INVOICE_UID_KEY , INVOICE_SEQ , INVOICE_NO , WAREHOUSE_KEY , SHIP_UID_KEY , SHIP_UID_ITEM_SEQ , SHIP_ORDER_KEY , SHIP_ORDER_ITEM_SEQ , GROUP_NO , WORK_BATCH_NO , SKU_CODE , SKU_NAME , SKU_SUB_NAME , QTY_SHIP_ORDER , CREATED_DATE , CREATED_TIME , CREATED_USER , MODIFIED_DATE , MODIFIED_TIME , MODIFIED_USER )
SELECT (SELECT INVOICE_UID_KEY FROM O_WCS.TB_DOC_INVOICE_HDR WHERE INVOICE_NO = #{item.invoiceNo}) , DTL.SHIP_UID_ITEM_SEQ , HDR.INVOICE_NO , HDR.WAREHOUSE_KEY , DTL.SHIP_UID_KEY , DTL.SHIP_UID_ITEM_SEQ , DTL.SHIP_ORDER_KEY , DTL.SHIP_ORDER_ITEM_SEQ , DTL.GROUP_NO , DTL.WORK_BATCH_NO , DTL.SKU_CODE , DTL.SKU_NAME , DTL.SKU_SUB_NAME , DTL.QTY_SHIP_ORDER , TO_CHAR(SYSDATE, 'YYYYMMDD') , TO_CHAR(SYSDATE, 'HH24MISS') , 'DAS_BATCH' , TO_CHAR(SYSDATE, 'YYYYMMDD') , TO_CHAR(SYSDATE, 'HH24MISS') , 'DAS_BATCH' FROM O_WCS.TB_ORD_SHIPMENT_HDR HDR , O_WCS.TB_ORD_SHIPMENT_DTL DTL WHERE HDR.SHIP_UID_KEY = DTL.SHIP_UID_KEY AND ORDER_NO = #{item.orderNo} AND HDR.INVOICE_NO = #{item.invoiceNo}
;
END }

/* [WcsToDasMapper.xml] updatePickingCompletList */ MERGE INTO O_WCS.TB_WIF_PICK_TOTE_HDR HDR USING (
SELECT WIF_PICK_TOTE_HDR_SEQ , #{item.dasIntfYn} AS DAS_INTF_YN FROM O_WCS.TB_WIF_PICK_TOTE_DTL WHERE TOTE_ID = #{item.toteId} AND WORK_BATCH_NO = #{item.workBatchNo} GROUP BY WIF_PICK_TOTE_HDR_SEQ
) TB ON( HDR.WIF_PICK_TOTE_HDR_SEQ = TB.WIF_PICK_TOTE_HDR_SEQ ) WHEN MATCHED THEN UPDATE
HDR.DAS_INTF_YN = TB.DAS_INTF_YN , HDR.DAS_INTF_DT = SYSDATE

	/* [WcsToDasMapper.xml] updatePickingCnclList */
	UPDATE O_WCS.TB_WIF_PICK_TOTE_HDR
	   SET DAS_CNCL_INTF_YN =  #{dasIntfYn}
	     , DAS_CNCL_INTF_DT =  SYSDATE
     WHERE WIF_PICK_TOTE_HDR_SEQ IN ( SELECT DISTINCT WIF_PICK_TOTE_HDR_SEQ
									    FROM O_WCS.TB_WIF_PICK_TOTE_DTL
									   WHERE TOTE_ID		= #{toteId}
									     AND WORK_BATCH_NO 	= #{workBatchNo})
	   AND TOTE_CNCL_YN = 'Y'
	   AND ALLOC_TYPE   = 'DAS'
